mapper.insert:
INSERT INTO follow (user_id, editor_id)
SELECT #{userId}, #{editorId}
    WHERE NOT EXISTS (
        SELECT 1
        FROM follow
        WHERE user_id = #{userId} AND editor_id = #{editorId}
    )
    AND EXISTS (
        SELECT 1
        FROM editor_detail
        WHERE editor_id = #{editorId}
    )

mapper.select:
SELECT count(editor_id) FROM follow

    WHERE user_id LIKE #{userId}


    WHERE user_id LIKE #{userId}


    WHERE nickname LIKE CONCAT('%',#{keyword},'%')

SELECT ed.editor_id , ed.user_id , ed.profile_image , u_editor.nickname , (SELECT COUNT(*) FROM follow f_sub WHERE f_sub.editor_id = ed.editor_id) AS count FROM follow f LEFT JOIN user u_user ON u_user.user_id = f.user_id LEFT JOIN editor_detail ed ON f.editor_id = ed.editor_id LEFT JOIN user u_editor ON u_editor.user_id = ed.user_id
WHERE u_user.user_id = #{userId}
WHERE u_user.user_id = #{userId}
WHERE nickname LIKE CONCAT('%',#{keyword},'%')
GROUP BY ed.editor_id, ed.user_id, ed.profile_image, u_editor.nickname ORDER BY f.created_at DESC LIMIT #{offset},#{countPerPage}
