mapper.select:
SELECT email
FROM user
WHERE email = #{email};

SELECT nickname
FROM user
WHERE user_id = #{userId}

SELECT vrm
FROM editor_detail
WHERE editor_id = #{editorId}

SELECT e.editor_id, profile_image, introduce, nickname, COALESCE(ROUND(avg(r.count / 2), 1), 0.0) as count, e.thumbnail, CASE WHEN round(avg(r.count)) = 1 THEN '☆' WHEN round(avg(r.count)) = 2 THEN '★' WHEN round(avg(r.count)) = 3 THEN '★☆' WHEN round(avg(r.count)) = 4 THEN '★★' WHEN round(avg(r.count)) = 5 THEN '★★☆' WHEN round(avg(r.count)) = 6 THEN '★★★' WHEN round(avg(r.count)) = 7 THEN '★★★☆' WHEN round(avg(r.count)) = 8 THEN '★★★★' WHEN round(avg(r.count)) = 9 THEN '★★★★☆' WHEN round(avg(r.count)) = 10 THEN '★★★★★' ELSE '' END AS star FROM editor_detail e LEFT JOIN user u ON e.user_id = u.user_id LEFT JOIN review r ON e.editor_id = r.editor_id
LEFT JOIN editor_tag et ON e.editor_id = et.editor_id WHERE et.tag LIKE CONCAT('%',#{keyword},'%')
GROUP BY e.editor_id, profile_image, introduce, nickname LIMIT #{offset},#{countPerPage}

SELECT *
FROM user AS u
WHERE u.email = #{email}

mapper.update:
